CREATE TABLE operating_systems (
	    `idOS` int not null auto_increment,
	    `name` varchar(50),
	    `version` varchar(10),
	    PRIMARY KEY(`idOS`)
) ENGINE = InnoDB;

CREATE TABLE uploaded_file (
	    `idWEFile` int not null auto_increment,
	    `name` varchar(300),
	    `filePath` varchar(300),
	    `description` varchar(5000),
	    `isData` tinyint(1),
	    PRIMARY KEY(`idWEFile`)
) ENGINE = InnoDB;

CREATE TABLE job_manager (
	    `jobManagerId` int not null auto_increment,
	    `jobManagerName` varchar(50),
	    PRIMARY KEY(`jobManagerId`)
) ENGINE = InnoDB;

CREATE TABLE backend (
	    `idBackend` int not null auto_increment,
	    `backendName` varchar(50),
	    `backendDesc` varchar(5000),
	    PRIMARY KEY(`idBackend`)
) ENGINE = InnoDB;

CREATE TABLE job_type (
	    `jobTypeId` int not null auto_increment,
	    `jobTypeName` varchar(50),
	    PRIMARY KEY(`jobTypeId`)
) ENGINE = InnoDB;

CREATE TABLE supported_job_managers (
	    `jobManagerId` int not null,
	    `idBackend` int not null,
	    FOREIGN KEY(`jobManagerId`) REFERENCES `job_manager`(`jobManagerId`),
	    FOREIGN KEY(`idBackend`) REFERENCES `backend`(`idBackend`)
) ENGINE = InnoDB;

CREATE TABLE workflow_engine_files (
	    `idWE` int not null,
	    `idWEFile` int not null,
	    FOREIGN KEY(`idWE`) references `platform`(`id`),
	    FOREIGN KEY(`idWEFile`) references `uploaded_file`(`idWEFile`)
) ENGINE = InnoDB;

