	    `backendInstName` varchar(50),
	    `idBackend` int not null,
	    `site` varchar(100),
	    `backendOutput` varchar(200),
	    `backendErrorOut` varchar(200),
	    `resource` varchar(100),
	    `jobManager` varchar(50),
	    `jobTypeId` int,
	    `idOS` int not null,
	    `we_dev` int not null,
	    PRIMARY KEY(`idBackendInst`),
	    FOREIGN KEY(`idOS`) REFERENCES `operating_systems`(`idOS`),
	    FOREIGN KEY(`we_dev`) REFERENCES `repo_user`(`id`),
	    FOREIGN KEY(`idBackend`) references `backend`(`idBackend`),
	    FOREIGN KEY(`jobTypeId`) references `job_type`(`jobTypeId`)
) ENGINE = InnoDB;


