	    `idWEImp` int not null auto_increment,
	    `nameWEImp` varchar(50),
	    `descriptionWEImp` varchar(5000),
	    `preDeployed` tinyint(1),
	    `shellPath` varchar(100),
	    `shellWEFileId` int,
