	    `idBackendInst` int not null,
	    `we_dev` int not null,
	    PRIMARY KEY(`idWEImp`),
	    FOREIGN KEY(`idWE`) REFERENCES `platform`(`id`),
	    FOREIGN KEY(`we_dev`) REFERENCES `repo_user`(`id`),
	    FOREIGN KEY(`idBackendInst`) REFERENCES `be_instance`(`idBackendInst`),
	    FOREIGN KEY(`shellWEFileId`) REFERENCES `uploaded_file`(`idWEFile`),
	    FOREIGN KEY(`zipWEFileId`) REFERENCES `uploaded_file`(`idWEFile`)
) ENGINE = InnoDB;


